#!/bin/bash

add_to_cart_base64="";

printf "Content-type: text/html\n\n"

if [ -z "$QUERY_STRING" ]
	then
		echo "<html><head></head><body><p>error: # loader.sh # error generating loader page with \$QUERY_STRING '$QUERY_STRING'</p></body></html>" && exit 1;
		
fi

QUERY=$(sed -E 's/^([^&]+)&session=.+/\1/gi' <<< "$QUERY_STRING");
session=$(sed -E 's/^[^&]+&session=(.+)/\1/gi' <<< "$QUERY_STRING");

if ( egrep -qioe '%[0-9]{2}' <<< "$QUERY" )
	then
		QUERY_SAFE=$(printf "$(sed -E 's/%([0-9\n]{2})/\\x\1/gi' <<< "$QUERY")\n" | sed -E 's/\/$//gi')
	else
		QUERY_SAFE=$(sed -E 's/\/$//gi' <<< "$QUERY");
fi

if [ ! -d "$QUERY_SAFE" ]
	then
		echo "$QUERY_SAFE is not a dir" && exit 1;
fi
count="1";
until ( [ "$(pwd)" == "$QUERY_SAFE" ] || [ "$count" -gt "10" ] )
	do
		cd "$QUERY_SAFE";
		count=$((++count));
		sleep 1.5;
done

if [ "$(pwd)" != "$QUERY_SAFE" ]
	then
		echo "error: # loader.sh # couldn't navigate to $QUERY_SAFE" && exit 3;
fi

if ( [ ! -e hash ] || [ ! -e poster.jpg ] || [ ! -e trailer.mp4 ] )
	then
		if [ ! -e hash ]
			then
				echo "error: # loader.sh # hash not in $QUERY_SAFE" && exit 4;
		fi

		if [ ! -e poster.jpg ]
			then
				echo "error: # loader.sh # poster.jpg not in $QUERY_SAFE" && exit 5;
		fi

		if [ ! -e trailer.mp4 ]
			then
				echo "error: # loader.sh # trailer.mp4 not in $QUERY_SAFE" && exit 6;
		fi
fi

hash="$(cat hash)";

if ( ! egrep -qioe '[0-9a-z]{32}' <<< "$hash" )
	then
		echo "error: # loader.sh # malformed hash" && exit 7;
fi

featureTitle="$(sed -E 's/^.+\/([^\/]+)$/\1/gi' <<< "$QUERY_SAFE")"
feature_dir_url="$(sed -E 's/\/var\/www\/html\/movie\///gi' <<< "$QUERY_SAFE")";
button_html="<html><head></head><body><center><a href=\"http://mov.ez/cgi-bin/queue?$hash&session=$session\" style='padding:auto; margin: auto; text-decoration: none; font-family: sans-serif;'><img height=\"50px\" src=\"data:image/png;base64,$add_to_cart_base64\"></a></center></body></html>";
base64_enc="$(base64 -w 0 --ignore-garbage <<< "$button_html")";
frame='<iframe class="neu_inset" src="data:text/html;base64,'"$base64_enc"'">iframe not supported</iframe>';
echo '<!DOCTYPE html><html><head><link rel="shortcut icon" href="http://mov.ez/img/favicon.ico"><link rel="stylesheet" type="text/css" href="http://mov.ez/css/main.css"><link rel="stylesheet" type="text/css" href="http://mov.ez/css/series-specific.css"><title>'"$featureTitle"'</title></head><body><div class="container"><div class="header neu-passive"><a href="http://mov.ez/"><div class="home_button neu"><img src="http://mov.ez/img/home.png"></div></a><div class="other-offerings"><div class="offerings-container"><a href="http://mov.ez/cgi-bin/moviesy_series/main.sh?session='"$session"'"><div class="offering neu"><img src="http://mov.ez/img/tv-100.png"></div></a><a href="http://mov.ez/cgi-bin/moviesy_feature/main.sh?page=1&session='"$session"'"><div class="offering neu"><img src="http://mov.ez/img/movies-100.png"></div></a><a href="http://mov.ez/cgi-bin/moviesy_korean/main.sh?session='"$session"'"><div class="offering neu"><img src="http://mov.ez/img/korean.png"></div></a></div></div><div class="search_bar"><form action="http://mov.ez/cgi-bin/search_feature.sh" method="get" enctype="application/x-www-form-urlencoded" autocomplete="off" novalidate><input class="neu" type="text" placeholder="Search" name="query'"$session"'"><img class="search_icon_style" src="http://mov.ez/img/search_icon.png"></form></div></div><div class="landing-feature neu-passive"><div class="left-col"><div class="title"><center><span>'"$featureTitle"'</span></center></div><div class="poster"><center><img src="http://mov.ez/movie/'"$feature_dir_url"'/poster.jpg"></center></div><div class="shop">'"$frame"'</div></div><div class="right-col neu_passive_inset"><video controls autoplay="true" poster="http://mov.ez/movie/'"$feature_dir_url"'/poster.jpg"><source src="http://mov.ez/movie/'"$feature_dir_url"'/trailer.mp4" type="video/mp4"></video></div></div></div></body></html>';

